function addEventListenersToLetters(){const letterElements=document.querySelectorAll(".letter"),imageElement=document.querySelector(".image"),popup=document.getElementById("overlay"),popupClose=document.getElementById("close-overlay");popupClose.addEventListener("click",()=>{popup.classList.remove("overlay-show")}),letterElements.forEach(element=>{element.addEventListener("click",()=>{const letterValue=element.getAttribute("data-letter");console.log("Letter element clicked:",letterValue),popup.classList.add("overlay-show")}),element.addEventListener("mouseover",()=>{const letterValue=element.getAttribute("data-letter"),imageElement=document.querySelector(".img-"+letterValue),newImageUrl="img/Letters-Hover/"+letterValue+".gif";imageElement.src=newImageUrl}),element.addEventListener("mouseout",()=>{const letterValue=element.getAttribute("data-letter"),imageElement=document.querySelector(".img-"+letterValue),newImageUrl="img/Letters/"+letterValue+".png";imageElement.src=newImageUrl})})}function getRandomLetter(){const alphabet="abcdefghijklmnopqrstuvwxyz",randomIndex=Math.floor(Math.random()*alphabet.length);return alphabet[randomIndex]}let mouseMoveTimeout,randomLetterInterval;function startRandomLetterTimer(){clearInterval(randomLetterInterval),randomLetterInterval=setInterval(()=>{const randomLetter=getRandomLetter();console.log(randomLetter)},1e3)}function resetMouseMoveTimer(){clearTimeout(mouseMoveTimeout),clearInterval(randomLetterInterval),mouseMoveTimeout=setTimeout(()=>{startRandomLetterTimer()},5e3)}addEventListenersToLetters(),document.addEventListener("mousemove",resetMouseMoveTimer),resetMouseMoveTimer();